import {Link, useNavigate} from 'react-router-dom'

function Login({setUser}){
    const navigate = useNavigate()

    function handleSubmit(e){
        e.preventDefault()
        setUser(e.target.username.value)
        navigate("/user")
    }

    return(
        <div>
            <form onSubmit={(e)=> handleSubmit(e)}>
                <input name="username"/>
            </form>
        </div>
    )
}
export default Login
